// encrypt decrrept
 function encrypt( $string ) 
{  
	$key="logic_erp_2011".date("YMD")."2012_platform";
	$result = ""; 
	for($i=0; $i<strlen($string); $i++) {
		$char = substr($string, $i, 1); 
		$keychar = substr($key, ($i % strlen($key))-1, 1); 
		$char = chr(ord($char)+ord($keychar)); 
		$result.=$char; 
	}		
	return base64_encode($result); 
}
function decrypt( $string ) 
{
	$key="logic_erp_2011".date("YMD")."2012_platform";
	
	$result = ""; 
	$string = base64_decode($string);		
	for($i=0; $i<strlen($string); $i++) {
		$char = substr($string, $i, 1); 
		$keychar = substr($key, ($i % strlen($key))-1, 1); 
		$char = chr(ord($char)-ord($keychar)); 
		$result.=$char; 
	}		
	return $result; 
}




function fnc_date_formate_bootstrp($txt_date_of_birth){
	$date_of_birth=explode("/",$txt_date_of_birth);
	$dob=$date_of_birth[2]."-".$date_of_birth[0]."-".$date_of_birth[1];
	return $dob;
	
}


function fnc_date_formate_eval_bootstrp($txt_date_of_birth){
	$date_of_birth=explode("-",$txt_date_of_birth);
	$dob=$date_of_birth[1]."/".$date_of_birth[2]."/".$date_of_birth[0];
	return $dob;
	
}

